# python import pandas as pdimport sysimport os try:	pop = sys.argv[1]	print("\nRead the following popgenome stats file:")	print(pop)except:	print("\nNo popgenome stats file given, quitting")	sys.exit()	try:	ms = sys.argv[2]	print("\nRead the following msstats file:")	print(pop)except:	print("\nNo msstats file given, quitting")	sys.exit()	try:	outpath = sys.argv[3]	print("\nRead the following output filepath:")	print(outpath)except:	print("\nNo output filepath given, quitting")	sys.exit()	df_pop = pd.read_table(pop)df_pop.head()df_ms = pd.read_table(ms,usecols=[1,3,5,7,9],names=["pi","ss","D","thetaH","H"])	df_ms.head()	df_concat = pd.concat([df_pop.reset_index(), df_ms], axis=1)df_concat = df_concat.drop("index",axis="columns")print("\nOutputting combined file.\n\n\n")df_concat.to_csv(outpath,sep="\t",index=False)